import { useCart } from '../context/CartContext';

const CartOverlay = () => {
  const { 
    cart, 
    isCartOpen, 
    setIsCartOpen,
    removeFromCart,
    updateQuantity,
    clearCart,
    getTotal 
  } = useCart();

  console.log('ðŸŽ¯ CartOverlay render - isCartOpen:', isCartOpen);

  if (!isCartOpen) {
    return null;
  }

  return (
    <div 
      className="cart-overlay" 
      onClick={(e) => {
        if (e.target === e.currentTarget) {
          console.log('âŒ Closing cart from overlay click');
          setIsCartOpen(false);
        }
      }}
    >
      <div 
        className="cart-added" 
        onClick={(e) => e.stopPropagation()}
      >
        <span 
          className="close-cart" 
          onClick={() => {
            console.log('âŒ Closing cart from close button');
            setIsCartOpen(false);
          }}
          role="button"
          tabIndex="0"
        >
          <i className="fa-sharp fa-solid fa-rectangle-xmark"></i>
        </span>
        <h3>Added to cart</h3>
        
        <div className="cart-content">
          {cart.length === 0 ? (
            <p style={{ textAlign: 'center', padding: '20px', color: '#666' }}>
              Your cart is empty
            </p>
          ) : (
            cart.map(item => (
              <div key={item.id} className="cart-item">
                <img src={item.image} alt={item.name} />
                <div className="item-details">
                  <h4>{item.name}</h4>
                  <p>${item.price}</p>
                  <div className="quantity-controls">
                    <button onClick={() => updateQuantity(item.id, item.quantity - 1)}>
                      -
                    </button>
                    <span>{item.quantity}</span>
                    <button onClick={() => updateQuantity(item.id, item.quantity + 1)}>
                      +
                    </button>
                  </div>
                  <button 
                    className="remove-btn"
                    onClick={() => removeFromCart(item.id)}
                  >
                    remove
                  </button>
                </div>
              </div>
            ))
          )}
        </div>
        
        {cart.length > 0 && (
          <div className="cart-footer">
            <h3>Your total : $ <span className="cart-total">{getTotal()}</span></h3>
            <button 
              className="clear-cart" 
              onClick={() => {
                console.log('âŒ Clearing cart');
                clearCart();
              }}
            >
              clear cart
            </button>
          </div>
        )}
      </div>
    </div>
  );
};

export default CartOverlay;
