import { useCart } from '../context/CartContext';

const CartOverlay = () => {
  const { 
    cart, 
    isCartOpen, 
    setIsCartOpen,
    removeFromCart,
    updateQuantity,
    clearCart,
    getTotal 
  } = useCart();

  if (!isCartOpen) return null;

  // ✅ إغلاق فقط عند الضغط على الخلفية
  const handleOverlayClick = (e) => {
    // تأكد أن الضغطة على الـ overlay نفسه مش على الـ cart-added
    if (e.target.classList.contains('cart-overlay')) {
      setIsCartOpen(false);
    }
  };

  return (
    <div 
      className="cart-overlay" 
      onClick={handleOverlayClick}
    >
      <div className="cart-added">
        <span 
          className="close-cart" 
          onClick={() => setIsCartOpen(false)}
          role="button"
          tabIndex="0"
        >
          <i className="fa-sharp fa-solid fa-rectangle-xmark"></i>
        </span>
        <h3>Added to cart</h3>
        
        <div className="cart-content">
          {cart.length === 0 ? (
            <p style={{ textAlign: 'center', padding: '20px', color: '#666' }}>
              Your cart is empty
            </p>
          ) : (
            cart.map(item => (
              <div key={item.id} className="cart-item">
                <img src={item.image} alt={item.name} />
                <div className="item-details">
                  <h4>{item.name}</h4>
                  <p>${item.price}</p>
                  <div className="quantity-controls">
                    <button onClick={() => updateQuantity(item.id, item.quantity - 1)}>
                      -
                    </button>
                    <span>{item.quantity}</span>
                    <button onClick={() => updateQuantity(item.id, item.quantity + 1)}>
                      +
                    </button>
                  </div>
                  <button 
                    className="remove-btn"
                    onClick={() => removeFromCart(item.id)}
                  >
                    remove
                  </button>
                </div>
              </div>
            ))
          )}
        </div>
        
        {cart.length > 0 && (
          <div className="cart-footer">
            <h3>Your total : $ <span className="cart-total">{getTotal()}</span></h3>
            <button 
              className="clear-cart normal" 
              onClick={clearCart}
            >
              clear cart
            </button>
          </div>
        )}
      </div>
    </div>
  );
};

export default CartOverlay;
